import factory
import factory.fuzzy
from factory import django

from leagues.models import League
from sports.tests.factories.SportFactory import SportFactory


class LeagueFactory(django.DjangoModelFactory):
    class Meta:
        model = League

    full_name = factory.sequence(lambda x: 'National Sport_{x} League'.format(x=x))
    # abbreviated named is autogenerated via overridden .save() method on the model
    sport = factory.SubFactory(SportFactory)
